443.source:
function display() {
    // display the polygon.  Just get the display points and turn them
    // into a string, set the clip mode to be visible (we've already adjusted
    // to our bounds) and grab the display string
    var polyPointString = ""
    this.displayPoints.forEach(function(aPoint) {
        polyPointString += aPoint.x + "," + aPoint.y + " "
    })

    this.setClipMode('visible')
    var html = "<svg height=" + this.getMaxDisplayPoint().y + " width=" + this.getMaxDisplayPoint().x + ">"
    html += '<polygon points="' + polyPointString + '"' + this.svgStyleString() + '/>'
    html += 'Sorry, your browser does not support SVG</svg>'
    this.jQuery().html(html)
}

446.source:
function svgStyleString() {
    // turn the svg style structure into a string
    var result = "style="
    Object.keys(this.svgStyle).forEach(function(aKey) {
        result +=  aKey+":" +this.svgStyle[aKey] + ";"
    }.bind(this))
    return result;
}

449.source:
function normalizePoints() {
    // normalize so that the top left is at (0,0)
    // do we really want to do this?
    var minPoint = this.polygonPoints[0].copy()
    this.polygonPoints.forEach(function(aPoint) {
        minPoint.x = Math.min(minPoint.x, aPoint.x)
        minPoint.y = Math.min(minPoint.y, aPoint.y)
    })
    this.polygonPoints = this.polygonPoints.map(function(aPoint) {
        return aPoint.subPt(minPoint)
    })
}

452.source:
function getDesiredExtent() {
    // get how big this would like to be
    return this.getMaxPoint().addPt(this.morphicBoundsOffset)
}

455.source:
function rescaleToNewExtent() {
    // calculate the display points of the polygon.  The big idea is that 
    // the user-specified points are invariant, but the display points are 
    // what are used to fit within whatever bounds we have.  The reason we 
    // don't just scale the polygonPoints and use those is that if we make
    // the polygon really small, the points go to (0,0) and then can't rescale
    var sizeToFit = this.getExtent().subPt(this.morphicBoundsOffset)
    var innerSize = this.getMaxPoint()
    // remember the scale factor for later use (e.g, addDisplayPoint()
    this.scaleFactor = pt(sizeToFit.x/innerSize.x, sizeToFit.y/innerSize.y)
    this.displayPoints = this.polygonPoints.map(function(aPoint) {
        return aPoint.scaleByPt(this.scaleFactor)
    }.bind(this))

}

458.source:
function getMaxPoint() {
    // get the maximum (x, y) of the user-set polygon points
    var maxPoint = this.polygonPoints[0].copy()
    this.polygonPoints.forEach(function(aPoint) {
        maxPoint.x = Math.max(maxPoint.x, aPoint.x)
        maxPoint.y = Math.max(maxPoint.y, aPoint.y)
    })
    return maxPoint
}

461.source:
function applyLayout() {
    // implement the layouter interface.   This is how we reshape to new
    // extents automatically on a scale
    this.rescaleToNewExtent()
    this.display()
}

