function checkParameter(name, value, minValue) {
    if(!value || isNaN(value)) {
        alert(name + ' must be specified, default used')
        return minValue
    }
    if (value < minValue) {
        alert(name + ' must be >= ' + minValue + '. ' + minValue + ' used instead')
        return minValue
    }
    return value
}

