function makeBlockArrowCommand(topLeft, bodyWidth, bodyLength, headWidth, headLength, double) {
    // do the SVG commands to make a block arrow, whose left top is 
    // topLeft  
    // start by computing all the horizontal and vertical lines we'll need
    topLeft.x = Math.round(topLeft.x)
    topLeft.y = Math.round(topLeft.y)

    var bodyLeft = double?topLeft.x + headLength: topLeft.x
    var bodyRight = bodyLeft + bodyLength
    var rightEdge = bodyRight + headLength
    // vertical positions
    var vertCenter = topLeft.y + Math.max(bodyWidth, headWidth)/2
    var bodyTop = vertCenter - bodyWidth/2
    var headTop = vertCenter - headWidth/2
    var bodyBot = vertCenter + bodyWidth/2
    var headBot = vertCenter + headWidth/2
    var svgCommand = ' '
    if (double) {
        svgCommand += 'M' + topLeft.x + ' ' + vertCenter + ' '
        svgCommand += 'L' + bodyLeft + ' ' + headTop + ' '
        svgCommand += 'L' + bodyLeft + ' ' + bodyTop + ' '
    } else {
        svgCommand += 'M' + topLeft.x + ' ' + bodyTop + ' '
    }
    svgCommand += 'L' + bodyRight + ' ' + bodyTop + ' '
    svgCommand += 'L' + bodyRight + ' ' + headTop + ' '
    svgCommand += 'L' + rightEdge + ' ' + vertCenter + ' '
    svgCommand += 'L' + bodyRight + ' ' + headBot + ' '
    svgCommand += 'L' + bodyRight + ' ' + bodyBot + ' '
    svgCommand += 'L' + bodyLeft + ' ' + bodyBot + ' '
    if (double) {
       svgCommand += 'L' + bodyLeft + ' ' + headBot + ' ' 
    }
    svgCommand += 'Z' 
    return svgCommand; // break this out for debugging
    // test
    // this.makeBlockArrowCommand(pt(0, 0), 20, 60, 40, 20, true)
    // M0 20 L40 0 L40 10 L60 10 L60 0 L100 20 L60 40 L60 30 L40 30 L40 40 Z
    // this.makeBlockArrowCommand(pt(25.13247, 63.1295), 20, 60, 40, 20, true)
    // M25 83 L65 63 L65 73 L85 73 L85 63 L125 83 L85 103 L85 93 L65 93 L65 103 Z
    // this.display()

}

