1688.source:
function setupCommandStructure(aCommand) {
    // The path commands.   Give both the long form and the short, so this 
    // can be called from either an edit initializer or the menu.  This
    // only needs to be run once or when changed
    this.commands = {
        'Move': {fields: ['Destination'], command:'M', checked:false},
        'Line': {fields: ['Destination'], command:'L', checked:false},
        'Horizontal': {fields: ['Destination'], command: 'H', checked:false},
        'Vertical': {fields: ['Destination'], command: 'V', checked:false},
        'Curve': {fields: ['ControlPoint1', 'ControlPoint2', 'Destination'], command:'C', checked:false},
        'S': {fields: ['ControlPoint1', 'Destination'], command:'S', checked:false},
        'Quadratic': {fields: ['ControlPoint1', 'Destination'], command:'Q', checked:false},
        'T': {fields: ['Destination'], command:'T', checked:false},
        'Arc': {fields: ['ControlPoint1', 'ArcFlags', 'Destination'], command: 'A', checked:false},
        'Close': {fields: [], command:'Z', checked:false},
    }
    var keys = Object.keys(this.commands);
    // add the menu field -- name for the menu item to show for this
    keys.forEach(function(aCommand) {
        this.commands[aCommand].menu = aCommand
    }.bind(this))
    // add the short forms, both upper-case (absolute) and lower-case (relative)

    keys.forEach(function(aKey) {
        var commandLetter = this.commands[aKey].command
        this.commands[commandLetter] = {fields:this.commands[aKey].fields, command:commandLetter, menu:aKey, checked:false}
        this.commands[commandLetter.toLowerCase()] = {fields:this.commands[aKey].fields, command:commandLetter.toLowerCase(), menu:aKey, checked:true}

    }.bind(this))



}

1694.source:
function getSVGCommand() {
    var svgCommand = this.get('Relative').isChecked()?this.commandArg.toLowerCase():this.commandArg.toUpperCase()
    var controlFields = ['ControlPoint1', 'ControlPoint2']
    controlFields.forEach(function(aField) {
        if (this.fieldsInUse.indexOf(aField) < 0) return;
        var valAsPt = this.get(aField).getValuesAsPoint()
        svgCommand += valAsPt.x + ' ' + valAsPt.y + ' '
    }.bind(this))
    if ('Aa'.indexOf(this.commandArg) >= 0) {
        svgCommand += this.arcFlagString() 
    }
    if (this.fieldsInUse.indexOf('Destination') >= 0) {
        var valAsPt = this.get('Destination').getValuesAsPoint()
        svgCommand += valAsPt.x + ' ' + valAsPt.y + ' '
    }
    return svgCommand
    // this.getSVGCommand()
}

