1700.source:
function setupForCommand(aCommand){
    // initialize with aCommand,returns false if the command is bad, true otherwise

    if(!this.commands.hasOwnProperty(aCommand)) {
        alert('Bad command ' + aCommand)
        return false;
    }

    // have the fields that we need for the command we're doing

    var pointFields = ['Destination', 'ControlPoint1', 'ControlPoint2', 'ArcFlags']
    pointFields.forEach(function(aField) {
        if (this.commands[aCommand].fields.indexOf(aField) == -1) {
            this.fields[aField].remove()
        } else {
            this.addMorph(this.fields[aField])
        }
    }.bind(this))

    // note the morphs we're going to use 
    this.fieldsInUse = this.commands[aCommand].fields
    // note the last command we set up for so we don't reinitialize for the same
    // command (can get into an infinite loop with the selection list)
    this.lastCommandSelected = this.commands[aCommand].menu
    this.get('CommandList').setSelection(this.commands[aCommand].menu)
    this.commandArg = this.commands[aCommand].command
    this.get('Relative').setChecked(this.commands[aCommand].checked)
    return true
}

