function getSVGPathCommand() {
    // for each command, build the string to do the command
    // this is basically just grabbing the command from the structure, converting
    // the points into x,y (instead of pt(x,y)) and making sure that they are of
    // integers.  There's an exception for A and a, which have unscaled args,
    // so we have to format those
    var commandString  = ""
    // need to end with a Z or z?
    this.svgCommands.forEach(function(aCommand) {
        commandString += this.getCommandString(aCommand)
    }.bind(this))

    return commandString
    // tests
    // this.svgCommands=this.generateTestSequences()[0]; this.getSVGPathCommand()
    // M150,0 L75,200 L225,200 Z
    // this.svgCommands=this.generateTestSequences()[1]; this.getSVGPathCommand()
