    // M 175,200 l 150,300
    // this.svgCommands=this.generateTestSequences()[3]; this.getSVGPathCommand()
    // M 175,200 l 150,0
    // this.svgCommands=this.generateTestSequences()[4]; this.getSVGPathCommand()
    // M 100,350 q 150,-300 300,0

