2050.source:
function makePolygon(numSides, sideLength) {
    // this.makePolygon(5, 50)
    // no borders -- we will make sure that the left edge and top are both
    // zero
    if (!numSides || numSides < 3) {
        alert("Minimum number of sides is 3")
        numSides = 3
    } 
    if (!sideLength || sideLength < 5) {
        alert("Minimum side length  is 5")
        sideLength = 5
    } 
    // radius of the polygon -- distance from each vertex to the center
    var radius = sideLength/(2*Math.sin(Math.PI/numSides))
    // center is at (radius, radius)
    var center = pt(radius, radius)
    // size is twice the radius + morphic offset
    this.setExtent(pt(2*radius, 2*radius).addPt(this.morphicBoundsOffset))
    // interior angle between two adjacent  radii of the polygon
    var angleIncrement = 2 * Math.PI/numSides
    // angle for the first radius.  This is vertical (3PI/2) for an odd-sided
    // polygon so that one vertex is at the top edge; for an even-sided polygon,
    // we want a flat edge across the top, so the angle is PI/2 + angleIncrement/2
    // This makes the first vertex on the top and just past the vertical 
    var startAngle = (numSides % 2 == 1)?3*Math.PI/2:(3*Math.PI + angleIncrement)/2
    // compute the angles for each vertex, in the coordinates of the center
    var angles = Array.range(0, numSides - 1).map(function(aNumber) {return startAngle + aNumber * angleIncrement});

    // for angle a, the corresponding vertex is at radius * (cos a, sin a)
    // in the coordinates of the 
    this.polygonPoints = angles.map(function(anAngle) {
        return center.addPt(pt(radius * Math.cos(anAngle), radius * Math.sin(anAngle)))
    })
    this.rescaleToNewExtent()
    this.display()

}

2053.source:
function makeArrow(bodyWidth, bodyLength, headWidth, headLength, twoSided) {
    // this.makeArrow(30, 50, 40, 20, true)
    // draws a horizontal arrow, pointing right (or both ways if two-sided is true).
    // bodyWidth: width (top-bottom) of the arrow body
    // bodyLength: length (left-right) of the arrow body
    // headWidth: width (top-bottom) of the arrow heqd(s)
    // headLength: length (left-right) of the arrow head(s)
    // twoSided: true for a double arrow
    var arrowParams = {
        'bodyWidth': 5,
        'bodyLength': 10,
        'headWidth': 5,
        'headLength': 5
    }
    bodyWidth = this.checkParameter('bodyWidth', bodyWidth, arrowParams.bodyWidth)
    bodyLength = this.checkParameter('bodyLength', bodyLength, arrowParams.bodyLength)
    headLength = this.checkParameter('headLength', headLength, arrowParams.headLength)
    headWidth = this.checkParameter('headWidth', headWidth, bodyWidth)
    // set the top and bottom of the body.  The top of the head is 0, the bottom of
    // the head will be this.getExtent().y.  The horizontal axis of the 
    // body is headWidth/2, so the top edge is that - bodyWidth/2, and the bottom 
    // edge is that + bottom/2
    var bodyTopY = (headWidth - bodyWidth)/2
    var bodyBotY = (headWidth + bodyWidth)/2
    var horizontalCenter = headWidth/2
    if (twoSided) {
        this.setExtent(pt(2*headLength + bodyLength, headWidth))
        this.polygonPoints = [pt(0,horizontalCenter), pt(headLength, 0), pt(headLength, bodyTopY),
        pt(headLength + bodyLength, bodyTopY), pt(headLength + bodyLength, 0),
        pt (2* headLength + bodyLength, horizontalCenter), pt(headLength + bodyLength, headWidth),
        pt(headLength + bodyLength, bodyBotY), pt(headLength, bodyBotY), pt(headLength, headWidth)]
    } else {
        this.setExtent(pt(headLength + bodyLength, headWidth))
        this.polygonPoints = [ pt(0, bodyTopY), pt(bodyLength, bodyTopY), pt(bodyLength, 0),
        pt (headLength + bodyLength, horizontalCenter), pt(bodyLength, headWidth),
        pt(bodyLength, bodyBotY), pt(0, bodyBotY)]
    }
    this.rescaleToNewExtent()
    this.display()
}

