2216.source:
function makeRegularStarSVGCommand(numPoints, pointHeight, vertexHeight) {

    // A star is essentially an n-sided regular polygon with a vertex between 
    // each pair of points.  So the angle between points is 2Pi/n and the angle
    // between point and vertex is therefore Pi/n

    var radialAngle = Math.PI/numPoints

    // we will enumerate the vertices going from the point.  In
    // a coordinate system where x increases to the left and y increases down,
    // the top of the figure is at -PI/2 radians, and we will alwayes have a 
    // point there.  
    var angle = -Math.PI/2
    var vertices = []
    // enumerate the vertices and points
    Array.range(0, numPoints - 1).forEach(function(index) {
        vertices.push(pt(Math.cos(angle), Math.sin(angle)).scaleBy(pointHeight).roundTo(0.1))
        angle += radialAngle
        vertices.push(pt(Math.cos(angle), Math.sin(angle)).scaleBy(vertexHeight).roundTo(0.1))
        angle += radialAngle
    })


    var centerOffset = pt(pointHeight, pointHeight).roundTo(0.1)
    var verticesInMorphCoordinates = vertices.map(function(aVertex) {return (aVertex.addPt(centerOffset)).roundTo(0.1)})
    var svgCommand = ''
    var charCommand = 'M'
    verticesInMorphCoordinates.forEach(function(aVertex) {
        svgCommand += charCommand + aVertex.x + ' ' + aVertex.y + ' '
        charCommand = 'L'
    })
    svgCommand += 'Z'
    return svgCommand
    // test:
    // this.makeRegularStarSVGCommand(5,50, 20)
    // M50 0 L61.8 33.8 L97.6 34.5 L69 56.2 L79.4 90.5 L50 70 L20.6 90.5 L31 56.2 L2.4 34.5 L38.2 33.8 Z
    // this.makeRegularPolygonSVGCommand(4,100, 40)
    // M20.700000000000003 20.700000000000003 L120.7 20.700000000000003 L120.7 120.7 L20.700000000000003 120.7 Z


}

