function init(aCommand, tilePalette) {
    // initialize with aCommand and tilePalette, which is the owner
    // of this tile.  returns false if the command is bad, true otherwise
    // execute once, when all fields are present in the scene graph
    // this.fields = {}; ['Destination', 'ControlPoint1', 'ControlPoint2', 'ArcFlags'].forEach(function(aField) { this.fields[aField] = this.get(aField)}.bind(this))
    // this.fields always has all three of the point fields

    // make sure that the command structure has been initialized
    if (!this.hasOwnProperty('commands')) {
        this.setupCommandStructure()
    }

    this.tilePalette = tilePalette
    this.lastCommandSelected = null;
    return this.setupForCommand(aCommand)

}

