function setFields(destination, controlPoints) {
    // destination is a point or nil, controlPoints is an array of Points
    if ('zZ'.indexOf(this.commandArg) >= 0) {
        // no args, do nothing
        return
    }
    if (this.fieldsInUse.indexOf('Destination') >= 0) {
        this.setFieldValues('Destination', destination)
    }
    // I should error-check here, but the world should be sane...the
    // only place data for this comes from are the classes in 
    // PathShapes.js, and both they and we follow the SVG standard, which 
    // won't change
     if (this.fieldsInUse.indexOf('ControlPoint1') >= 0) {
        this.setFieldValues('ControlPoint1', controlPoints[0])
    }
    if (this.fieldsInUse.indexOf('ControlPoint2') >= 0) {
        this.setFieldValues('ControlPoint1', controlPoints[1])
    }
}

