function display() {
    // display the path.  
    var pathCommand = this.getSVGPathCommand()
    var boundingBox = this.getBoundingBoxForPath()
    var extent = boundingBox[1].subPt(boundingBox[0])
    this.setClipMode('visible')
    var html = "<svg height=" + this.getExtent().y + " width=" + this.getExtent().x 
    html += ' viewBox="' + boundingBox[0].x + ' ' + boundingBox[0].y + ' ' +
            extent.x + ' ' + extent.y + '">'
    html += '<path d="' + pathCommand + '"' + this.svgStyleString() + '/>'
    html += 'Sorry, your browser does not support SVG</svg>'
    this.jQuery().html(html)
}

