4215.source:
function addTile(aCommand) {
    // create a new tile with command aCommand.  Note that the list of 
    // commands is in commandTilePrototype.init() -- keys to the object
    // in that routine.  So make sure this is called with one of those.
    // newTile.init() returns false if it wasn't
    var newTile = this.commandTilePrototype.copy()
    if (newTile.init(aCommand, this)) {
        this.addMorph(newTile)
        this.tiles.push(newTile)
        this.positionTiles()
        return newTile;
    } else {
        alert("Could not create tile for command " + aCommand)
        return null
    }

}

4218.source:
function yCoordinateForIndex(anIndex) {
    // given an index of a tile, return the y coordinate corresponding to
    // that position
    return this.firstTileYPosition + anIndex * this.commandTilePrototype.getExtent().y
}

4224.source:
function indexForPosition(aPt) {
    // the inverse of yCoordinateForIndex -- given a point (x,y), what's the index
    // in the tile list?
    var raw = Math.round((aPt.y - this.firstTileYPosition)/this.commandTilePrototype.getExtent().y)
    if (raw < 0) return 0;
    if (raw >= this.tiles.length) return this.tiles.length - 1
    return raw;
}

4227.source:
function deleteTile(aTile) {
    var index = this.tiles.indexOf(aTile)
    if (index < 0) {
        alert("Request to delete non-existent tile!")
        // put in a hook so the programmer can see it
        Global.that = aTile;
        Global.that.show()
        return;
    }
    aTile.remove()
    this.tiles.splice(index, 1)
    this.positionTiles()

}

4230.source:
function tileMoved(aTile) {
    var index = this.tiles.indexOf(aTile)
    if (index < 0) {
        alert("Request to move a non-existent tile!")
        // put in a hook so the programmer can see it
        Global.that = aTile;
        Global.that.show()
        return;
    }
    // alert(aTile.getPosition())
    var newIndex = this.indexForPosition(aTile.getPosition())
    if (newIndex != index) {
        this.tiles.splice(index, 1)
        this.tiles.pushAt(aTile, newIndex)
    }
    this.positionTiles()

}

