function addTilesFromSVGPathMorph(anSVGPathMorph) {
    var pathElements = anSVGPathMorph.getPathElements()
    var svgObjectArray = anSVGPathMorph.createSVGObjectFromElements(pathElements)
    svgObjectArray.forEach(function(anSVGObject) {
        var tile = this.addTile(anSVGObject.command)
        if (anSVGObject.hasOwnProperty('dest')) {
