function display() {
    // display the polygon.  Just get the display points and turn them
    // into a string, set the clip mode to be visible (we've already adjusted
    // to our bounds) and grab the display string
    var polyPointString = ""
    this.displayPoints.forEach(function(aPoint) {
        polyPointString += aPoint.x + "," + aPoint.y + " "
    })

    this.setClipMode('visible')
    var html = "<svg height=" + this.getMaxDisplayPoint().y + " width=" + this.getMaxDisplayPoint().x + ">"
    html += '<polygon points="' + polyPointString + '"' + this.svgStyleString() + '/>'
    html += 'Sorry, your browser does not support SVG</svg>'
    this.jQuery().html(html)
}

