5330.source:
function makeRegularPolygonSVGCommand(numSides, sideLength) {

    // the radius of a polygon (distance from center to vertex)
    // is r = s/(2 * sin(Pi/n)).  See http://www.mathopenref.com/polygonradius.html

    var radius = sideLength/(2*Math.sin(Math.PI/numSides))
    // and the internal angle between radians is just 2PI/n
    var radialAngle = 2 * Math.PI/numSides
    // we will enumerate the vertices going from the top-left vertex.  In
    // a coordinate system where x increases to the left and y increases down,
    // the top of the figure is at -PI/2 radians.  An odd-sided figure
    // will have a vertex at -PI/2.  An even sided-figure has two vertices 
    // on opposite sides of -PI/2, and the first is at -(-PI + radialAngle)/2
    var startAngle = (numSides % 2 == 1)?-Math.PI/2:-(Math.PI + radialAngle)/2

    // compute the angles for the radians
    var radians = Array.range(0, numSides - 1).map(function(n) {return startAngle + n * radialAngle})
    // get the vertices in the coordinate system where the center is (0,0)
    // this will enumerate the vertices, with one vertex
    var vertices = radians.map(function(anAngle) {return pt(Math.cos(anAngle), Math.sin(anAngle)).scaleBy(radius).roundTo(0.1)})
    var centerOffset = pt(radius, radius).roundTo(0.1)
    var verticesInMorphCoordinates = vertices.map(function(aVertex) {return aVertex.addPt(centerOffset)})
    var svgCommand = ''
    var charCommand = 'M'
    verticesInMorphCoordinates.forEach(function(aVertex) {
        svgCommand += charCommand + aVertex.x + ' ' + aVertex.y + ' '
        charCommand = 'L'
    })
    svgCommand += 'Z'
    return svgCommand
    // test:
    // this.makeRegularPolygonSVGCommand(3,20)
    // M11.5 0 L21.5 17.3 L1.5 17.3 Z
    // this.makeRegularPolygonSVGCommand(4,20)
    // M4.1 4.1 L24.1 4.1 L24.1 24.1 L4.1 24.1 Z

}

5334.source:
function init(anActionStruct) {
    // the idea behind a pulldown Menu is that it's self-contained:
    // the only connection is to itself, and all the information to fill
    // the list and perform the actions is in the action struct
    // an ActionStruct is of the form
    // {title: aString,
    //  actions: {S1: F1,
    //            S2: F2...
    //             }
    // menuOrder: [S1, S2,]
    // }
    // the list is populated with aString, S1, S2,...
    // Fi is called as the action when Si is selected
    // optArg: menuOrder: a list with some of the keys specified.  These will
    // appear first in the list after the title, in order.  Other keys added
    // as Object.keys(anActionStruct.actions) fills them
    this.clearSelections()
    this.setList([])
    var keys = Object.keys(anActionStruct.actions)
    var itemList = [anActionStruct.title]
    anActionStruct.menuOrder.forEach(function(aString) {
        if (aString == anActionStruct.title) {
            alert("Error: " + aString + " can't be both a title and an action")
        } else if (itemList.indexOf(aString) != -1) {
            alert('Error: ' + aString + ' appears twice')
        } else if (keys.indexOf(aString) == -1) {
            alert("Error: " + aString + " appears in menuOrder but has no action")
        } else {
            itemList.push(aString)
        }
    })
    keys.forEach(function (aString) {
        if (itemList.indexOf(aString) != -1) return;
        if (aString == anActionStruct.title) {
            alert("Error: " + aString + " can't be both a title and an action")
            return
        } 
        itemList.push(aString)
    })
    this.setList(itemList)
    this.actions = anActionStruct.actions
    this.title = anActionStruct.title



}

5340.source:
function doAction(anItemFromList) {

    if(!anItemFromList) {
        return;
    }
    // generic action called on selection.  This should only be called
    // in response to a selection change, and then it just does 
    // this.actions[anItemFromList].action() and resets to the title
    if (anItemFromList == this.title) {
        // just resetting to the title, do nothing
        return;
    }
    // shouldn't happen, but suspenders & belt
    if (this.itemList.indexOf(anItemFromList) == -1) {
        alert("Error: " +  anItemFromList + " doesn't appear in the menu!")
    }
    // OK, just do the action and return.  This is guaranteed to be there
    this.actions[anItemFromList]()
    this.setSelection(this.title)

}

