6440.source:
function getCommandString(aCommand) {
    // get a string for a command.  Broken out as a separate method to 
    // make it easy to test.  Basically, turn pt(x, y) into the string x,y for 
    // each point, taking care to round to avoid 3.000001,1.999995, etc

    var ptString = ""
    if (aCommand.hasOwnProperty('args')) {
        aCommand.args.forEach(function(aPt) {
            ptString += Math.round(aPt.x) + "," + Math.round(aPt.y) + " "
        })
    }
    var result = aCommand.command + ptString;
    if (aCommand.hasOwnProperty('flags')) {
        result += aCommand.flags + " "
    }
    return result
    // tests
    // this.getCommandString({command:'a', args:[pt(4.0002, 25.0000)], flags:"0 0,0"})
    // should be a4,25 0 0,0 
    // this.getCommandString({command:'M', args:[pt(25,3.5785)]})
    // should be M25,4 
    // this.getCommandString({command:'Z'})
    // should be Z
    // this.getCommandString({command:'z', args:[]})z
    // should be z
    // this.getCommandString({command:'Q', args:[pt(10,20), pt(30,60)]})
    // should be Q10,20 30,60 
    // this.getCommandString({command:'C', args:[pt(10,20), pt(30,60), pt(45,74)]})
    // should be C10,20 30,60 45,74 
    // all results should have a trailing blank
}

6443.source:
function getBoundingBoxForCommand(start, aCommand) {
    // do the simple stuff first.

    var endPoint = this.endPointForCommand(start, aCommand)
    var minPt = start.minPt(endPoint)
    var maxPt = start.maxPt(endPoint)
    // bounding box for simple line and movement commands are just the endpoints
    if('mlhvzt'.indexOf(aCommand.command.toLowerCase()) >= 0) {
        return [minPt, maxPt]
    }
    // gotta look at the control points for arcs and splines
    // bit of a cheat: we aren't interpreting the SVG here, we're just getting 
    // the bounding box.  So for s,S, T, t, we've seen the control points, so 
    // we've got their bounds.  For splines, we're also assuming that the 
    // curve is contained within the bbox of [start, end, controlpoint]
    // single control point
    var controlPoints = [aCommand.args[0].copy()]
    if (aCommand.command.toLowerCase() == 'c') {
        controlPoints.push(aCommand.args[1].copy())
    }
    // Offset from the start point if required (lower-case) or if this is an
    // ellipse (Aa)
    if ('csqAa'.indexOf(aCommand.command) >= 0)  {
        controlPoints = controlPoints.map(function(aPt) {return aPt.addPt(start)})
    }

    // get the max and min
    controlPoints.forEach(function(aPt) {
        minPt = aPt.minPt(minPt)
        maxPt = aPt.maxPt(maxPt)
    })
    return [minPt, maxPt]
    // tests
    // this.getBoundingBoxForCommand(pt(0,0), {command:'M', args:[pt(150,-150)]})
    // should be: lively.pt(0.0,-150.0),lively.pt(150.0,0.0)
    // this.getBoundingBoxForCommand(pt(50,50), {command:'l', args:[pt(150,-150)]})
    // should lively.pt(50.0,-100.0),lively.pt(200.0,50.0)
    // this.getBoundingBoxForCommand(pt(50,50), {command:'A', args:[pt(60,40), pt(50,-150)]})
    // should be lively.pt(50.0,40.0),lively.pt(110.0,90.0)
    // this.getBoundingBoxForCommand(pt(50,50), {command:'C', args:[pt(60,-200), pt(300, 50), pt(50,-150)]})
    // should be lively.pt(50.0,-200.0),lively.pt(300.0,50.0)
}

6446.source:
function endPointForCommand(start, aCommand) {
    // do the simple stuff first.
    var endPointIndices = {m:0, l:0, h:0, v:0, c:2, s:1, q:1, t:0, a:0}
    if (aCommand.command.toLowerCase() == 'z') {
        return start
    }
    var endPointIndex = endPointIndices[aCommand.command.toLowerCase()]
    // alert(endPointIndex)
    var endPoint = aCommand.args[endPointIndex]
    // alert(endPoint)
    if (aCommand.command.toLowerCase() == aCommand.command) {
        endPoint = endPoint.addPt(start)
    }
    return endPoint

}

6449.source:
function getBoundingBoxForPath() {
    // this.getBoundingBoxForPath()
    // very simple: just walk through the commands, getting the bounding box 
    // for each command as a pair of points, and using this to expand the bounding
    // box as we go
    var start = pt(0,0)
    // get the bounding boxes for each command, one by one
    var bboxes = this.svgCommands.map(function(aCommand) {
        var box = this.getBoundingBoxForCommand(start, aCommand)
        start = this.endPointForCommand(start, aCommand)
        return box

    }.bind(this));
    // each bbox is a pair of points, [min, max].  Walk through the list, 
    // finding the min and max for the whole list
    var bbox = bboxes.reduce(function(prev, current) {
        return [prev[0].minPt(current[0]), prev[1].maxPt(current[1])]
    }, bboxes[0])
    return bbox

}

