6455.source:
function createCommand(commandName, args, flags) {
    // Check the command and create the structure
    // only use lower-case for checking
    // this.makeBlockArrow(20, 60, 40, 20, true, true)
    var svgPathCommandNames = {
        a:2, c:3, m:1, l:1, h:1, v:1, s:2, q:2, t:1, z:0
    }
    if (svgPathCommandNames.hasOwnProperty(commandName.toLowerCase())) {
        var numArgs = args?args.length:0
        var argCopy = args?args.map(function(aPt) {return(aPt.copy())}):[]
        var useFlags = 'aA'.indexOf(commandName) > 0
        if (numArgs == svgPathCommandNames[commandName.toLowerCase()]) {
            if (useFlags) {
                return {command:commandName, args:args, flags:flags}
            } else {
                return {command:commandName, args:args}
            }
        } else {
            alert("Expected " + svgPathCommandNames[commandName.toLowerCase()] + "  argument for command " + commandName + " given " + numArgs)
            Global.inspect(args)
            return null
        }
    } else {
        alert('Command ' + commandName + ' is invalid ')
        return null;
    }

}

