        // put in a hook so the programmer can see it
        Global.that = aTile;
        Global.that.show()
        return;
    }
    aTile.remove()
    this.tiles.splice(index, 1)
    this.positionTiles()

}

6492.source:
function tileMoved(aTile) {
    var index = this.tiles.indexOf(aTile)
    if (index < 0) {
        alert("Request to move a non-existent tile!")
        // put in a hook so the programmer can see it
        Global.that = aTile;
        Global.that.show()
        return;
    }
    // alert(aTile.getPosition())
    var newIndex = this.indexForPosition(aTile.getPosition())
    if (newIndex != index) {
        this.tiles.splice(index, 1)
        this.tiles.pushAt(aTile, newIndex)
    }
    this.positionTiles()

}

