function normalizePoints() {
    // normalize so that the top left is at (0,0)
    // do we really want to do this?
    var minPoint = this.polygonPoints[0].copy()
    this.polygonPoints.forEach(function(aPoint) {
        minPoint.x = Math.min(minPoint.x, aPoint.x)
        minPoint.y = Math.min(minPoint.y, aPoint.y)
    })
    this.polygonPoints = this.polygonPoints.map(function(aPoint) {
        return aPoint.subPt(minPoint)
    })
}

