6905.source:
function makeStar() {

    var numPoints = Number(this.get('NumPointsInput').getTextString()),
        pointHeight = Number(this.get('PointHeightInput').getTextString()),
        vertexHeight = Number(this.get('VertexHeightInput').getTextString())
    if (isNaN(numPoints) || numPoints < 3) {
        numPoints = 3
    }
    numPoints = Math.floor(numPoints)
    if (isNaN(pointHeight) || pointHeight < 40) {
        pointHeight = 40
    }
    if (isNaN(vertexHeight) || vertexHeight < 20) {
        vertexHeight = 20
    }
    if(vertexHeight > pointHeight - 20) {
        vertexHeight = pointHeight - 20
    }
    this.makeRegularStar(numPoints, pointHeight, vertexHeight)
    this.remove()

}

