function svgStyleStructure() {
    var result = {}
    var fillTag = this.get('IsFilled').isChecked()?this.get('FillColor').color:'none'
    result.fill = fillTag
    var strokeVal = this.get('StrokeWidthInput').getTextString()
    if (!isNaN(strokeVal) && strokeVal > 0) {
        result['stroke-width'] = strokeVal
        result.stroke = this.get('StrokeColor').color
    } 
    result.opacity = this.get('OpacityValue').value
    return result
}

