function init(anSVGPathMorph) {

    // if we aren't handed a polygon, we're going to create as well as edit
    // one.  so load one.  Then fill the style panel
    if(!anSVGPathMorph) {
        /* Old code from use of the HTML Wrapper around SVG Paths.  Now
           we are using the native Lively implementation
        var polyMaker = this;
        lively.PartsBin.getPart('SVGPathMorph', 'PartsBin/HTML', function(err, part) {
            if(err) {
                alert("Error in opening SVGPolygonMorph: " + err)

            }
            if (part) {
                polyMaker.svgPathMorph = part;
                part.openInWorldCenter();
                this.fillStylePanel()

            } else {
                this.remove()
            }

        }); */
        // newer, simpler code.  All right, this is beautiful...
        // this.svgPathMorph =  new lively.morphic.Path().openInWorld();
        // this.svgPathMorph = lively.PartsBin.getPart('SVGPathMorph', 'PartsBin/Basic')
        this.svgPathMorph = lively.PartsBin.getPart('SVGPathMorph', 'PartsBin/HTML')
        this.svgPathMorph.openInWorldCenter()
        this.fillStylePanel()

    } else {
        this.svgPathMorph = anSVGPathMorph;
        this.fillStylePanel()
    }


}

6924.source:
function updateMorph(svgCommand) {
    // this.svgPathMorph.setSVGStyle(this.svgStyleStructure())
    // this.svgPathMorph.display()
    this.lastCommand = svgCommand
    if (!this.svgPathMorph) {
        this.init()
    }
    var svgParse = lively.morphic.Shapes.PathElement.parse;
    var pathElements = svgParse(svgCommand)
    this.svgPathMorph.setPathElements(pathElements)
    this.svgPathMorph.setFill(this.get('FillColor').color)
    this.svgPathMorph.setBorderColor(this.get('StrokeColor').color)
    this.svgPathMorph.setBorderWidth(Number(this.get('StrokeWidthInput').getTextString()))
    this.svgPathMorph.setOpacity(this.get('OpacityValue').value)
}

