function setExtent(aPoint) {
    var current = this.getExtent()
    var scaleFactor = pt(aPoint.x/current.x, aPoint.y/current.y)
    this.shape.setVertices(this.shape.vertices().map(function(aVertex) {
        return aVertex.scaleByPt(scaleFactor)
    }))

}

