function addDiffFor(fileMorph) {
    var builder = this;
    var fileName = fileMorph.getFileName();
    var commands = [{
        name: "diff",
        gitCommand: "diff " + (fileMorph.fileInfo.status === 'staged' ? '--staged ' : '') + fileName
    }];

    this.runCommandsThenDo(commands, function(mapping) {
        var pos = fileMorph.bounds().topLeft().addXY(0,1),
            bounds = pos.extent(builder.getExtent().withY(40)),
            diffView = new lively.morphic.CodeEditor(bounds, mapping.diff);
        diffView.fileInfo = fileMorph.fileInfo;
        diffView.applyStyle({gutter: null, mode: 'diff'});
        builder.addMorph(diffView);
        diffView.name = 'diffFor:' + fileName;
        diffView.addScript(function stopScrollWhenBordersAreReached() {});
        diffView.addScript(function resize(layerConfig) {
            var ext = this.getExtent();
            var scrollWidth = this.aceEditor.renderer.scrollBar.getWidth();
            var height = layerConfig.maxHeight + scrollWidth;
            if (ext.y === height) return;
            this.setExtent(this.owner.getExtent().subXY(scrollWidth+this.getBorderWidth()*2+5, 0).withY(height));
            this.owner.applyLayout();
        });
        diffView.addScript(function getFileMorph() {
            var diff = this, fileName = this.name.split(':').last();
            return builder.findItems(function(item) {
                return item.isText && item.textString.trim() === fileName
                    && Objects.equal(item.fileInfo, diff.fileInfo); })[0]; 
        });
        diffView.addScript(function onKeyDown(evt) {
            var keys = evt.getKeyString();
            if (keys === 'S') {
                builder.gitItemStageOrUnstage(this.getFileMorph());
                evt.stop(); return true;                
            } else if (keys === 'K') {
                builder.gitItemReset(this.getFileMorph());
                evt.stop(); return true;                
            }
            return $super(evt);
        });
        diffView.withAceDo(function(ed) {
            lively.bindings.connect(ed.renderer, 'layerConfig', this, 'resize');
        })
        // builder.applyLayout();

    })
}

