function gitSwitchBranch() {
    var builder = this,
        commands = [{name: "branchList", isExec: true, command: 'git --no-pager branch --list --no-color | grep -v "*" | xargs -I BRANCH git --no-pager log -n 1 --format="BRANCH %ci" BRANCH -- '}];        
    builder.runCommandsThenDo(commands, function(mapping) {
        var branches = mapping.branchList
                .split('\n').invoke('trim')
                .map(function(line) {
                    var match = line.match(/([^\s]+)\s(.*)/);
                    return match && {name: match[1], date: new Date(match[2])}; })
                .compact().sortBy(function(ea) { return +ea.date; }).reverse();
            items = branches.map(function(branch) {
                return [branch.name, show.bind(null, 'Choosing ' + branch.name)]; }),
            newest = items.slice(0,10),
            oldest = items.slice(10);
        // we just show the 10 recently changed branches
        newest.push(['more...', oldest]);
        lively.morphic.Menu.openAtHand('Choose branch', newest);
    });
}

