733.source:
function sendGitCommand(commandString, thenDo) {
    // module("lively.ide.GitInterface").load()
    var cmd = lively.ide.CommandLineInterface.run(commandString, thenDo),
        output = this.get('Output');
    lively.bindings.connect(cmd, 'stdout', output, 'insertTextStringAt', {updater: function($upd, data) { $upd(null, data); }});
    lively.bindings.connect(cmd, 'stderr', output, 'insertTextStringAt', {updater: function($upd, data) { $upd(null, '\n' + data); }});
    lively.bindings.connect(cmd, 'code', output, 'insertTextStringAt', {updater: function($upd, data) { $upd(null, '\nexited: ' + data); }});
}

737.source:
function addCommandToHistory(cmd) {
    var hist = this.commandHistory,
        items = hist.items;
    if (items.last() === cmd) return;
    items.push(cmd);
    if (items.length <= hist.max) return;
    hist.items = items = items.slice(-hist.max);
    hist.index = items.length - 1;
}

749.source:
function showHistItem(dir) {
    dir = dir || 'next';
    var hist = this.commandHistory, items = hist.items, len = items.length-1, i = hist.index;
    if (!Numbers.between(i, 0, len-1)) hist.index = i = len;
    if (this.textString !== items[i]) { this.textString = items[i]; return; }
    if (dir === 'next') {
        if (i >= len) return;
        i = ++hist.index;
    } else {
        if (i <= 0) return;
        i = --hist.index;
    }
    this.textString = items[i];
}

770.source:
function onKeyDown(evt) {
    var sig = evt.getKeyString(),
        cmdLine = this.get('CommandLine'),
        output = this.get('Output');
    switch(sig) {
        case 'Tab': cmdLine.isFocused() ? output.focus() : cmdLine.focus(); evt.stop(); return true;
    }
    if (!cmdLine.isFocused()) return;
    switch(sig) {
        case 'Enter': cmdLine.commandLineInput(cmdLine.textString); evt.stop(); return true;
        case 'Alt-Up': output.focus(); evt.stop(); return true;
        case 'Alt-Down': cmdLine.focus(); evt.stop(); return true;
        case 'Up':
        case 'Control-Up': cmdLine.showPrevCommand(); cmdLine.focus(); evt.stop(); return true;
        case 'Down':
        case 'Control-Down': cmdLine.showNextCommand(); cmdLine.focus(); evt.stop(); return true;
        case 'Esc':
        case 'Control-C':
        case 'Control-G': cmdLine.clear(); evt.stop(); return true;
        default: $super(evt);        
    }
}

