4115.source:
function resetLayouter() {
    var l = this.getLayouter();
    l.orderedSubmorphs = function (submorphs) {
        return submorphs.reject(function(ea) { return ea.isEpiMorph || !ea.isLayoutable})
                        .sort(function(a,b) {
                            if (a.index < b.index) return -1;
                            if (a.index > b.index) return 1;
                            return a.bounds().topLeft().y - b.bounds().topLeft().y;
                        });
    }
}

