function buildLogLines(func) {
    /*
    this.initUpdate()
    */
    var commands = [{name: "log", gitCommand: "log --graph --pretty=format:'%Cred%h%Creset -%C(yellow)%d%Creset %s %Cgreen(%cr) %C(bold blue)<%an>%Creset' --abbrev-commit --date=relative -n 100 --all"}];
    this.runCommandsThenDo(commands, function(mapping) {
        var lines = mapping.log.split('\n'), result = {};
        result.logLines = lines;
        func(result);
    });
}

