function buildFileChange(type, statusString) {
    // var existing = this.findItems(function(ea) { return ea.name === fileName.trim() && ea.fileInfo.status === type; })[0];
    // if (existing) {
    //     existing.outdated = false;
    //     this.buildIndex = m.index + 1;
    //     show('reusing %s', fileName)
    //     return existing;
    // }
    // statusString looks like "R  bar.txt -> foo.txt"
    /*
    this.initUpdate(['fileInfo']);
    */
    var change = '';
    var statusFlag = statusString[type === 'staged' ? 0 : 1];
    statusString = statusString.slice(3);
    var fileName = statusString;

    if      (statusFlag === "M") { change = 'modfied'; }
    else if (statusFlag === "R") { change = 'renamed'; statusString = change + ': ' + statusString; fileName = fileName.split('->').last().trim(); }
    else if (statusFlag === "A") { change = 'added'; statusString = change + ': ' + statusString; }
    else if (statusFlag === "D") { change = 'deleted'; statusString = change + ': ' + statusString; }

    var m = this.buildText(statusString, {padding: lively.Rectangle.inset(8, 0, 0, 0)});
    m.addScript(function getFileName() { return this.name; });
    m.name = fileName.trim();
    m.gitActions = [];
    m.gitActions.push({name: 'browse', targetMethod: 'livelyBrowse', args: [m], buttonWidth: 62});
    if (type === 'unstaged') {
        m.gitActions.push({name: ' diff', targetMethod: 'gitItemToggleDiff', args: [m], buttonWidth: 62});
        m.gitActions.push({name: ' stage', targetMethod: 'gitItemStageOrUnstage', args: [m], buttonWidth: 62});
    } else if (type === 'staged') {
        m.gitActions.push({name: ' diff', targetMethod: 'gitItemToggleDiff', args: [m], buttonWidth: 62});
        m.gitActions.push({name: ' unstage', targetMethod: 'gitItemStageOrUnstage', args: [m], buttonWidth: 62});
    } else if (type === 'untracked') {
        m.gitActions.push({name: 'add', targetMethod: 'gitItemAdd', args: [m], buttonWidth: 62});
    }
    m.gitActions.push({name: '  x', targetMethod: 'gitItemReset', args: [m], buttonWidth: 22});
    return m;
}

