function buildFileInfo(func) {
    /*
    this.initUpdate()
    this.initUpdate(['fileInfo']);
    this.removeAllMorphs();
    this.initUpdate(['fileInfo']);
    this.buildFileInfo(show);
    */
    var commands = [{name: "status", gitCommand: "status --porcelain", transform: function(cmd) { return cmd.getStdout(); }},];
    this.runCommandsThenDo(commands, function(mapping) {
        var lines = mapping.status.split('\n'), result = {};
        result.unstaged = lines.map(function(line) { var m = line.match(/^(\s[A-Z]|[A-Z]{2})(.*)/); return m && line; }).compact(),
        result.staged = lines.map(function(line) { var m = line.match(/^[A-Z]{1,2}(.*)/); return m && line; }).compact(),
        result.untracked = lines.map(function(line) { var m = line.match(/^\s*\?\?(.*)/); return m && line; }).compact();
        func(result);
    })
}

