2460.source:
function updateProgress() {
    var todos = this.todoItems(),
        howMany = todos.length,
        howManyDone = todos.select(function(ea) { return ea.isDone() }).length;
    this.get('ProgressBar').setValue(Math.max(0.01, howManyDone / howMany));
}

