4649.source:
function setCwd(dir) {
    this.reset();
    if (dir) {
        dir = dir.replace(/^\.\//, '').replace(/^\.$/, '');
        var lkdir = URL.create(Config.nodeJSURL).asDirectory().
                        withFilename('NodeJSEvalServer/').asWebResource().
                        post("process.env.WORKSPACE_LK").content;
        dir = lkdir + "/" + dir;
    }
    this.cwd = dir;
    (function() { 
        this.activateTab(this.get('status'));
    }).bind(this).delay(0.3);
}
