function gitItemActionsShow(item) {
    if (item.showingGitActions || !item.gitActions) return;
    item.showingGitActions = true;
    var buttons = item.gitActionButtons = [];
    item.gitActions.forEach(function(action) {
        var btn = new lively.morphic.Button(pt(action.buttonWidth || 32, 16).extentAsRectangle(), action.name);
        btn.applyStyle({padding: lively.Rectangle.inset(1)});
        connect(btn, 'fire', this, action.targetMethod, {
            varMapping: {args: action.args || []},
            updater: function($upd) { $upd.apply(this, args); }});
        buttons.push(item.addMorph(btn));
    }, this);

    // layout
    var textBounds = $(item.getTextChunks().last().getChunkNode()).bounds(),
        startPos = item.localize(pt(textBounds.right, textBounds.top+textBounds.height()/2)).addXY(20, 0);
    // startPos.x = Math.max(startPos.x, this.minButtonOffset);
    buttons[0].align(buttons[0].bounds().leftCenter(), startPos);
    buttons.without(buttons[0]).forEach(function(ea, i) {
        ea.align(ea.bounds().leftCenter(), buttons[i].bounds().rightCenter());
    });
}

