function gitUnStageAll() {
    var builder = this,
        files = builder.findItems(function(item) { return item.fileInfo && item.getFileName && item.fileInfo.status === 'staged'}).invoke('getFileName'),
        commands = [{name: "reset", gitCommand: "reset -- " + files.join(' ')}];
