}

3481.source:
function initUpdate(steps) {
    /*
    this.initUpdate(['cleanup', 'logAllBranches']);
    */
    var builder = this;
    var defaultSteps = ['cleanup', 'logAllBranches'];
    var availableSteps = {
        cleanup: function(next) {
            builder.removeAllMorphs();
            next();
        },
        logCurrentBranch: function(next) {
        },
        logAllBranches: function(next) {
            builder.setExtent(pt(100,100));
            builder.buildLogLines(function(result) {
                builder.buildReset();
                builder.buildMoveTo(0);
                // builder.buildSet({basicInfo: result.onto});
                builder.buildSpacer(5);
                result.logLines.forEach(function(line) {
                    var richtText = lively.ide.CommandLineInterface.toStyleSpec(line),
                        m = builder.buildText(richtText.string, {fixedWidth: false});
                    m.emphasizeRanges(richtText.ranges);
                });
                next();
            });
        },

    }
    steps = steps || defaultSteps;
    builder.isInLayoutCycle = true;
    steps.map(function(step) { return availableSteps[step] }).doAndContinue(null, function() {
        builder.isInLayoutCycle = false;
        builder.applyLayout();
    });
}

3485.source:
function buildLogLines(func) {
    /*
    this.initUpdate()
    */
    var commands = [{
        name: "log",
        options: {ansiAttributeEscape: false},
        gitCommand: "log"
                  + " --graph"
                  + " --pretty=format:"
                  +   "'%Cred%h%Creset "
                  +   "-%C(green)%d%Creset "
                  +   "%C(bold)%s%Creset "
                  +   "%Cblue(%cr, %an)%Creset'"
                  + " --abbrev-commit"
                  + " --date=relative"
                  + " -n 100"
                  + " --all"}];
    this.runCommandsThenDo(commands, function(mapping) {
        var lines = mapping.log.split('\n'), result = {};
        result.logLines = lines;
        func(result);
    });
}

