function resetLayouter() {
    var l = this.getLayouter();
    l.setBorderSize({left: 15, top: 5, right: 15, bottom: 2});
    l.orderedSubmorphs = function (submorphs) {
        return submorphs.reject(function(ea) { return ea.isEpiMorph || !ea.isLayoutable})
                        .sort(function(a,b) {
                            if (a.index < b.index) return -1;
                            if (a.index > b.index) return 1;
                            return a.bounds().topLeft().y - b.bounds().topLeft().y;
                        });
    }
}

250.source:
function gitSwitchBranch() {
    var builder = this,
        commands = [{name: "branchList", isExec: true, command: 'git --no-pager branch --no-color | grep -v "*" | xargs -I BRANCH git --no-pager log -n 1 --format="BRANCH %ci" BRANCH -- '}];        
    builder.runCommandsThenDo(commands, function(mapping) {
        var branches = mapping.branchList
                .split('\n').invoke('trim')
                .map(function(line) {
                    var match = line.match(/([^\s]+)\s(.*)/);
                    return match && {name: match[1], date: new Date(match[2])}; })
                .compact().sortBy(function(ea) { return +ea.date; }).reverse();
            items = branches.map(function(branch) {
                return [branch.name, show.bind(null, 'Choosing ' + branch.name)]; }),
            newest = items.slice(0,10),
            oldest = items.slice(10);
        // we just show the 10 recently changed branches
        if (oldest.length > 0) newest.push(['more...', oldest]);
        lively.morphic.Menu.openAtHand('Choose branch', newest);
    });
}

