function gitPullRemoteChanges() {
    var builder = this;
    builder.notify('Pulling changes...');
    var commands = [{name: "pull", gitCommand: "pull --rebase"}];
    builder.runCommandsThenDo(commands, function(mapping) {
        builder.notify(mapping.pull);
    });
}

290.source:
function gitPushCurrentBranch() {
    var builder = this;
    builder.notify('Pushing changes...');
    var commands = [
        {name: "localBranch", gitCommand: "symbolic-ref HEAD", transform: function(cmd) { var out = cmd.getStdout().trim(); return out.slice(out.lastIndexOf('/')+1); }},
        {name: "remote",      gitCommand: "config branch.${localBranch}.remote" /*--> "origin"*/}]
    builder.runCommandsThenDo(commands, function(mapping) {
        if (!mapping.localBranch || !mapping.remote) {
            builder.notify('Cannot pull because local branch or remote repository are not set locally.\n'
                         + 'branch: ' + mapping.localBranch + ', remote: ' + mapping.remote);
            return;
        }
        var commands = [{
            name: "push",
            gitCommand: "push " + mapping.remote + ' ' + mapping.localBranch,
            transform: function(cmd) { return (cmd.getStdout() + ' ' + cmd.getStderr()).trim(); }}];
        builder.runCommandsThenDo(commands, function(mapping) {
            builder.notify("Push: " + mapping.push);
        });
    });
}

294.source:
function notify(message, howLong) {
    /*
    builder.findItems(function(item) { return item.isNotify; }).invoke('remove')
    item = builder.findItems(function(item) { return item.isNotify; }).last()
    this.notify('test\n123');
    item.remove()
    */
    if (howLong === undefined) {
        howLong = 5000;
        var lineCount = message.split('\n').length - 1;
        howLong += lineCount * 2000; // display 2secs longer for each line
    }
    howLong = howLong || 5000;
    var builder = this;
    builder.buildMoveTo(0); builder.buildSetOverwrite(false);
    builder.buildSet({isNotify: true});
    var item = builder.buildText(message)
    item.gitActions = [{name: 'x', targetMethod: 'notifyRemove', args: [item], buttonWidth: 62}];
    var existing = builder.findItems(function(item) { return item.isNotify; }).last();
    if (existing) { item.index = existing.index+1; item.setPosition(existing.bounds().bottomLeft()); };
    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-
    item.addStyleClassName('gitNotification');
    // item.setAppearanceStylingMode(true);
    item.setBorderStylingMode(true);
    item.setTextStylingMode(true);
    item.setPadding(lively.Rectangle.inset(5));
    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-
    item.fit();
    builder.applyLayout();
    item.removeTimeout = item.setOpacityAnimated.bind(item,0, 2000, item.remove.bind(item)).delay(howLong/1000);
}

