3477.source:
function initUpdate(steps) {
    /*
    this.initUpdate(['cleanup', 'logAllBranches']);
    */
    var builder = this;
    var defaultSteps = ['cleanup', 'logAllBranches'];
    var availableSteps = {
        cleanup: function(next) {
            builder.removeAllMorphs();
            next();
        },
        logCurrentBranch: function(next) {
        },
        logAllBranches: function(next) {
            builder.setExtent(pt(100,100));
            builder.buildLogLines(function(result) {
                builder.buildReset();
                builder.buildMoveTo(0);
                // builder.buildSet({basicInfo: result.onto});
                builder.buildSpacer(5);
                result.logLines.forEach(function(line) {
                    builder.buildText(' ' + line, {fixedWidth: false});
                });
                next();
            });
        },

    }
    steps = steps || defaultSteps;
    builder.isInLayoutCycle = true;
    steps.map(function(step) { return availableSteps[step] }).doAndContinue(null, function() {
        builder.isInLayoutCycle = false;
        builder.applyLayout();
    });
}

