1571.source:
function buildFileChange(type, statusString) {
    // statusString looks like "R  bar.txt -> foo.txt"
    /*
    this.initUpdate(['fileInfo']);
    */
//        +o   ' ' = unmodified
//        +o    _M = modified
//        +o    _A = added
//        +o    _D = deleted
//        +o    _R = renamed
//        +o    _C = copied
//        +o    _U = updated but unmerged
// 
//        Ignored files are not listed, unless --ignored option is in effect, in
//        which case XY are !!.
// 
//            X          Y     Meaning
//            -------------------------------------------------
//                      [MD]   not updated
//            M        [ MD]   updated in index
//            A        [ MD]   added to index
//            D         [ M]   deleted from index
//            R        [ MD]   renamed in index
//            C        [ MD]   copied in index
//            [MARC]           index and work tree matches
//            [ MARC]     M    work tree changed since index
//            [ MARC]     D    deleted in work tree
//            -------------------------------------------------
//            D           D    unmerged, both deleted
//            A           U    unmerged, added by us
//            U           D    unmerged, deleted by them
//            U           A    unmerged, added by them
//            D           U    unmerged, deleted by us
//            A           A    unmerged, both added
//            U           U    unmerged, both modified
//            -------------------------------------------------
//            ?           ?    untracked
//            !           !    ignored
//            -------------------------------------------------

    var change = '',
        statusFlags = statusString.slice(0,2), // git status --porcelain format
        statusFlagIndex = statusFlags[0],
        statusFlagWorkTree = statusFlags[1],
        statusFlag = type === 'unstaged' ? statusFlagWorkTree : statusFlagIndex;
    statusString = statusString.slice(3);
    var fileName = statusString;
    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-
    // for unmerged changes the status flags can be interpreted as follows:
    if (statusFlags ==="DD") change = "unmerged, deleted locally and remotely";
    else if (statusFlags ==="AU") change = "unmerged, added locally and modified remotely";
    else if (statusFlags ==="UD") change = "unmerged, modified locally and deleted remotely";
    else if (statusFlags ==="UA") change = "unmerged, modified locally and added remotely";
    else if (statusFlags ==="DU") change = "unmerged, deleted locally and modified remotely";
    else if (statusFlags ==="AA") change = "unmerged, added locally and remotely";
    else if (statusFlags ==="UU") change = "unmerged, modified locally and remotely";
    else if (statusFlag === "M") change = 'modfied';
    else if (statusFlag === "R") change = 'renamed';
    else if (statusFlag === "C") change = 'copied';
    else if (statusFlag === "A") change = 'added';
    else if (statusFlag === "D") change = 'deleted';
    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-
    if (change.startsWith('unmerged') && type === 'staged') return; // just display unmerged changes once
    if (change !== 'modified' && type != 'untracked') { // for modified we need no further explanation
        statusString = change + ': ' + statusString;
        if (change === 'renamed' || change === 'copied')
            fileName = fileName.split('->').last().trim();
    }
    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-
    var m = this.buildText(statusString, {padding: lively.Rectangle.inset(8, 0, 0, 0)});
    m.fileInfo = Object.extend({name: fileName.trim(), change: change}, m.fileInfo);
    m.addScript(function getFileName() { return this.fileInfo.name; });
    m.name = fileName;
    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-
    m.gitActions = [];
    m.gitActions.push({name: 'browse', targetMethod: 'livelyBrowse', args: [m], buttonWidth: 62});
    if (type === 'unstaged') {
        m.gitActions.push({name: ' diff', targetMethod: 'gitItemToggleDiff', args: [m], buttonWidth: 62});
        m.gitActions.push({name: ' stage', targetMethod: 'gitItemStageOrUnstage', args: [m], buttonWidth: 62});
    } else if (type === 'staged') {
        m.gitActions.push({name: ' diff', targetMethod: 'gitItemToggleDiff', args: [m], buttonWidth: 62});
        m.gitActions.push({name: ' unstage', targetMethod: 'gitItemStageOrUnstage', args: [m], buttonWidth: 62});
    } else if (type === 'untracked') {
        m.gitActions.push({name: 'add', targetMethod: 'gitItemAdd', args: [m], buttonWidth: 62});
    }
    m.gitActions.push({name: '  x', targetMethod: 'interactiveGitItemReset', args: [m], buttonWidth: 22});
    return m;
}

1574.source:
function resetLayouter() {
    var l = this.getLayouter();
    l.setBorderSize({left: 15, top: 5, right: 15, bottom: 2});
    l.orderedSubmorphs = function (submorphs) {
        return submorphs.reject(function(ea) { return ea.isEpiMorph || !ea.isLayoutable})
                        .sort(function(a,b) {
                            if (a.isNotify && b.isNotify) return a.time > b.time;
                            if (a.isNotify && !b.isNotify) return -1;
                            if (!a.isNotify && b.isNotify) return 1;
                            if (a.index < b.index) return -1;
                            if (a.index > b.index) return 1;
                            return a.bounds().topLeft().y - b.bounds().topLeft().y;
                        });
    }
    l.layoutOrder = function(m) {
        if (m.isNotify) return -m.time;
        if (m.index) return m.index;
        return m.bounds().y;
    }
    // this.applyLayout()
    // l.basicLayout = l.basicLayout.getOriginal().wrap(function(proceed, container, submorphs) {
    //     return proceed(container, submorphs);
    // });
}

