function gitCommit() {
    var builder = this;
    this.checkForUserNameAndEmail(function(err) {
        if (err) {
             builder.notify('Git needs to know your name/email: ' + err);
             return;
        }
        doCommit();
    })
    function doCommit() {
        var w = builder.world();
        w.editPrompt('Please enter a commit message:', function(message) {
            if (!message) {
                builder.notify('No commit message, commit aborted.'); return;
            }
            var commands = [{
                name: "commit",
                gitCommand: "commit -m \"" + message.replace(/"/g, '\\"') + "\"",
                transform: function(cmd) {
                    cmd.code ? cmd.getStderr() : cmd.getStdout();
                }
            }];
            builder.runCommandsThenDo(commands, function(mapping) {
                builder.initUpdate(['cleanup', 'fileInfo']);
            });
        }, "empty commit message");
    }
}

