function addCodeViewFor(targetMorph, itemInfoPropName, codeViewPrefix, text, mode) {
    text = text || '';
    mode = mode || 'text';
    var builder = this;
    var targetName = targetMorph[itemInfoPropName].name;
    var pos = targetMorph.bounds().topLeft().addXY(0,1),
        bounds = pos.extent(builder.getExtent().withY(40)),
        codeView = new lively.morphic.CodeEditor(bounds, text);
    codeView[itemInfoPropName] = targetMorph[itemInfoPropName];
    codeView.index = targetMorph.index;
    codeView.applyStyle({gutter: null, mode: mode});
    codeView.name = codeViewPrefix + ':' + targetName;
    codeView.itemInfoPropName = itemInfoPropName;
    codeView.creator = builder;
    codeView.addScript(function getTargetMorph() {
        var self = this;//, targetName = this.name.split(':').last();
        return this.creator.findItems(function(item) {
            return item[self.itemInfoPropName]
                && Objects.equal(item[self.itemInfoPropName], self[self.itemInfoPropName]); })[0];
    });
    // -=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-=-
    codeView.addScript(function stopScrollWhenBordersAreReached() {});
    codeView.addScript(function resize() {
        var ext = this.getExtent(),
            layerConfig = this.aceEditor.renderer.layerConfig,
            scrollWidth = this.aceEditor.renderer.scrollBar.getWidth(),
            height = layerConfig.maxHeight + scrollWidth;
        if (ext.y === height) return;
        this.setExtent(this.owner.getExtent().subXY(scrollWidth+this.getBorderWidth()*2+5, 0).withY(height));
        this.owner.applyLayout();
    });
    codeView.withAceDo(function() { connect(this, 'textChange', this, 'resize'); });
    codeView.whenOpenedInWorld(function() { this.resize.bind(this).delay(0.4); });
    return builder.addMorph(codeView);
}

