function showHistItem(dir) {
    dir = dir || 'next';
    var hist = this.commandHistory, items = hist.items, len = items.length-1, i = hist.index;
    if (!Numbers.between(i, 0, len-1)) hist.index = i = len;
    if (this.textString !== items[i]) { this.textString = items[i]; return; }
    if (dir === 'next') {
        if (i >= len) return;
        i = ++hist.index;
    } else {
        if (i <= 0) return;
        i = --hist.index;
    }
    this.textString = items[i];
}

1858.source:
function onKeyDown(evt) {
    var sig = evt.getKeyString(),
        cmdLine = this.get('CommandLine'),
        output = this.get('Output');
    switch(sig) {
        case 'Tab': cmdLine.isFocused() ? output.focus() : cmdLine.focus(); evt.stop(); return true;
    }
    if (!cmdLine.isFocused()) return;
    switch(sig) {
        case 'Enter': cmdLine.commandLineInput(cmdLine.textString); evt.stop(); return true;
        case 'Alt-Up': output.focus(); evt.stop(); return true;
        case 'Alt-Down': cmdLine.focus(); evt.stop(); return true;
        case 'Up':
        case 'Control-Up': cmdLine.showPrevCommand(); cmdLine.focus(); evt.stop(); return true;
        case 'Down':
        case 'Control-Down': cmdLine.showNextCommand(); cmdLine.focus(); evt.stop(); return true;
        case 'Esc':
        case 'Control-C':
        case 'Control-G': cmdLine.clear(); evt.stop(); return true;
        default: $super(evt);        
    }
}

