function reset() {
    connect(this.get('CheckBox'), 'checked', this, 'done');
    connect(this, 'done', this.get('CheckBox'), 'setChecked');
    connect(this, 'done', this, 'onDoneChange', {converter: function() { return null }});
    connect(this.get('removeButton'), 'fire', this, 'remove');
    this.isTodoItem = true;
    this.get('Text').addScript(function onTabPressed(evt) {
        evt.stop();
        this.owner.selectNext(evt.isShiftDown());
        return true;
    });
    this.get('Text').addScript(function onKeyDown(evt) {
        var result = $super(evt);
        var char = String.fromCharCode(evt.keyCode).toLowerCase();
        this.ctrlCPressed = this.ctrlCPressed || 0;
        if (char === 'c' && evt.isCtrlDown()) { this.ctrlCPressed++; }
        else { this.ctrlCPressed = 0 }
        if (this.ctrlCPressed === 2) {
             this.ctrlCPressed = 0;
             this.owner.done = !this.owner.done;
        }
        return result
    });
}

