function addTodoItem() {
    var item = this.get('TodoItemProto').copy();
    item.setVisible(true);
    this.addMorph(item).setPosition(this.innerBounds().bottomLeft());
    var self = this;
    // there is a layouting issue when layting manually so trigger layout again...
    (function() { self.applyLayout() }).delay(0);
    this.updateProgress();
    return item;
}

3825.source:
function reset() {
    // this.get('TodoItemProto').setVisible(true);
    // proto = this.get('TodoItemProto').copy(); this.addMorph(proto); proto.setPosition(pt(0,0));
    // proto.setVisible(false); proto.name = 'TodoItemProto';
    connect(this.get('addItemButton'), 'fire', this, 'addTodoItem');
    this.applyStyle({clipMode: 'auto'});
}

3828.source:
function updateProgress() {
    var todos = this.todoItems(),
        howMany = todos.length,
        howManyDone = todos.select(function(ea) { return ea.isDone() }).length;
    this.get('ProgressBar').setValue(Math.max(0.01, howManyDone / howMany));
}

