3841.source:
function todoItems() {
    return this.submorphs
        .select(function(ea) { return ea.isTodoItem })
        .without(this.get('TodoItemProto'));
}

3844.source:
function todoItemNext(item, before) {
    if (!item) return null;
    var sorted = this.todoItems().sortBy(function(ea) { return ea.getPosition().y }),
        itemIdx = sorted.indexOf(item);
    if (!before && sorted.length-1 === itemIdx) {
        sorted.push(this.addTodoItem());
    }
    var nextIdx = Math.min(sorted.length - 1, Math.max(0, itemIdx + (before ? -1 : 1)));
    return sorted[nextIdx];
}

3847.source:
function focusTodoItemAfter(item, prev) {
    var next = this.todoItemNext(item, prev);
    next && next.todoText().focus();
}

3930.source:
function doAction() {
    var win = this.get('GitControl'),
        statusTab = this.get('status'),
        container= statusTab.owner;
    [function(next) {
        container.activateTab(statusTab);
        next.delay(2);
    },
    function(next) {
        win.reset()
        next.delay(0.5);
    },
    function(next) {
         dlg = $world.openPublishPartDialogFor(win);
         // dlg is removed on successful commit
         connect(dlg, 'remove', {cb: function() {
            msg = dlg.get('CommitMessageText').textString;
            next.delay(0.3);
         }}, 'cb', {removeAfterUpdate: true});
    },
    function(next) {
        commit = Strings.format('git commit -m "%s" -- %s %s %s',
            msg, "core/lively/ide/tools/GitControl.html", "core/lively/ide/tools/GitControl.json", "core/lively/ide/tools/GitControl.metainfo");
        show(lively.shell.execSync(commit));
        next();
    }].doAndContinue(null, show.curry('Published GitControl'));
}

