function morphMenuItems() {
    var self = this,
        world = this.world(),
        items = $super();
    items.push([
        'Set working directory', function(evt) {
            require('lively.ide.tools.FileTree').toRun(function() {
                var fTree = lively.BuildSpec('lively.ide.tools.FileTree').createMorph().openInWorldCenter();
                fTree.get("fileTree").withFileTreeDo = self.withFileTreeDo.bind(fTree.get('fileTree'));
                fTree.targetMorph.update();
                fTree.get("fileTree").update = function(f) {
                    var tree = fTree.get('tree'),
                        selection = tree.selection,
                        path = "";
                    while (selection != tree) {
                        path = "/" + selection.item.name + path;
                        selection = selection.parent;
                    }
                    fTree.initiateShutdown();
                    self.cwd = lively.ide.CommandLineInterface.getWorkingDirectory() + path;
                    self.get('GitLog').reset();
                    self.get('GitStatus').reset();
                }
                fTree.get('UpdateButton').setLabel("Open");
                fTree.comeForward();
            });
        }
    ]);
    return items;
}

