function resetLayouter() {
    var l = this.getLayouter();
    l.setBorderSize({left: 15, top: 5, right: 15, bottom: 2});
    l.orderedSubmorphs = function (submorphs) {
        return submorphs.reject(function(ea) { return ea.isEpiMorph || !ea.isLayoutable})
                        .sort(function(a,b) {
                            if (a.isNotify && b.isNotify) return a.time > b.time;
                            if (a.isNotify && !b.isNotify) return -1;
                            if (!a.isNotify && b.isNotify) return 1;
                            if (a.index < b.index) return -1;
                            if (a.index > b.index) return 1;
                            return a.bounds().topLeft().y - b.bounds().topLeft().y;
                        });
    }
    l.layoutOrder = function(m) {
        if (m.isNotify) return -m.time;
        if (m.index) return m.index;
        return m.bounds().y;
    }
    // this.applyLayout()
    // l.basicLayout = l.basicLayout.getOriginal().wrap(function(proceed, container, submorphs) {
    //     return proceed(container, submorphs);
    // });
}

