    next && next.todoText().focus();
}

2557.source:
function doAction() {
    var win = this.get('GitControl'),
        statusTab = this.get('status'),
        container= statusTab.owner;
    [function(next) {
        container.activateTab(statusTab);
        next.delay(2);
    },
    function(next) {
        win.reset()
        next.delay(0.5);
    },
    function(next) {
         dlg = $world.openPublishPartDialogFor(win);
         // dlg is removed on successful commit
         connect(dlg, 'remove', {cb: function() {
            msg = dlg.get('CommitMessageText').textString;
            next.delay(0.3);
         }}, 'cb', {removeAfterUpdate: true});
    },
    function(next) {
        commit = Strings.format('git commit -m "%s" -- %s %s %s',
            msg, "core/lively/ide/tools/GitControl.html", "core/lively/ide/tools/GitControl.json", "core/lively/ide/tools/GitControl.metainfo");
        show(lively.shell.execSync(commit));
        next();
    }].doAndContinue(null, show.curry('Published GitControl'));
}

