function buildText(string, style) {
    var m = lively.BuildSpec({
        className: 'lively.morphic.Text',
        textString: string,
        _Extent: pt(this.maxWidth, 20),
        style: {
            fontSize: 9, fontFamily: 'DejaVu Sans Mono,Monaco,monospace',
            fixedHeight: false, fixedWidth: true,
            enableGrabbing: false, //enableDragging: false,
            allowInput: false
        },
        onDoubleClick: function onDoubleClick(evt) {
            if (!this.inputAllowed()) this.applyStyle({allowInput: !this.inputAllowed()});
            return true;
        },
        onBlur: function onBlur(evt) {
            if (this.inputAllowed()) {
                this.applyStyle({allowInput: false});
            }
        }

    }).createMorph();
    return this.addBuildMorph(m, style);
}

